		endReceiveData()
	}
	targetContainer.appendChild(div);
	targetContainer.setAttribute("tag", 1)
}

//获取所有有效标签
var findAllMedias = function (target, from) {
	var currUrl = window.location.href;
	console.log("currUrl: " + currUrl);
	if (/https?:\/\/.*instagram\.com\/stories\.*\//.test(currUrl)) {
		//story页面 查找所有的section标签
		if (target.className == "section") {
			handleSection(target)
		} else {
			var sections = target.getElementsByTagName("section")
			if (sections.length > 0) {
				for (var i = 0; i < sections.length; i++) {
					handleSection(sections[i])
				}
			}
		}
	} else {
		if (/https?:\/\/.*instagram\.com\/p\/(?!.*chaining=true)/.test(currUrl)) {
			//story页面 查找所有的section标签
			if (target.className == "section") {
				handleArticle(target, true)
			} else {
				var sections = target.getElementsByTagName("section")
				if (sections.length > 0) {
					for (var i = 0; i < sections.length; i++) {
						var sectionE = sections[i]
						var mainE = sectionE.children[0]
						if (mainE != null) {
							var navE = mainE.children[0]
							if (navE != null && navE.tagName == "NAV") {
								var articleSameE = mainE.children[1]
								if (articleSameE != null) {
									handleArticle(articleSameE, true)
								}
							}
						}
					}
				}
			}
		}

		// 找到所有的article标签
		if (target.className == "article") {
			handleArticle(target, false)
		} else {
			var articles = target.getElementsByTagName("article")
			if (articles.length > 0) {
				var hasMedia = true;
				if (articles.length > 4) {
					hasMedia = false; // 需要检测
				}
				for (var i = 0; i < articles.length; i++) {
					var isValid = handleArticle(articles[i], false)
					if (isValid) {
						hasMedia = true;
					}
				}
				// if (!hasMedia) { // 上报异常
				// 	reportError("has article no photo/video");
				// }
			} else {
				// reel页面
				var mainE = document.getElementsByTagName("main")[0]
				if (mainE != null) {
					var mainChildOne = mainE.children[0]
					if (mainChildOne != null) {
						var listParent = mainChildOne.children[0]
						if (listParent != null) {
							var list = listParent.childNodes
							for (var i = 0; i < list.length; i++) {
								var mediaItemE = list[i];
								var videoInfoPPE = mediaItemE.children[0]
								if (videoInfoPPE != null) {
									var videoE = videoInfoPPE.getElementsByTagName("video")[0] // 视频信息
									if (videoE != null) {
										tagRealVideo(videoE.src, mediaItemE, videoInfoPPE)
									}
								}
							}
						}
					}
				}

			}
		}
	}
}

var handleArticle = function (article, isPost) {
	var isValid = false
	var ulE = article.getElementsByTagName("ul")[0]
	if (ulE != null) {
		var liEs = article.getElementsByTagName("li")
		if (liEs.length > 0) {
			for (var idx = 0; idx < liEs.length; idx++) {
				var liE = liEs[idx]
				var imgE = liE.getElementsByTagName("img")[0]
				if (imgE != null) {
					isValid = true
					tagMedia(article.children[0], imgE, liE, false, isPost)
				} else {
					var videoE = liE.getElementsByTagName("video")[0]
					if (videoE != null) {
						isValid = true
						tagMedia(article.children[0], videoE, liE, false, isPost)
					}
				}
			}
		}
	} else {
		var rootNode = article.children[0]
		if (rootNode != null) {
			var contentRoot = rootNode.children[1]
			if (contentRoot != null) {
				var imgs = contentRoot.getElementsByTagName("img")
				var imgE = imgs[0]
				if (imgs.length == 1 && imgE.parentNode.className != "ins_dl") {
					tagMedia(rootNode, imgs[0], imgs[0].parentNode, false, isPost)
				} else {
					var videos = contentRoot.getElementsByTagName("video")
					if (videos.length == 1) {
						tagMedia(rootNode, videos[0], videos[0].parentNode, false, isPost)
					}
				}
			}
		}
	}
	return isValid;
}

var handleSection = function (section) {
	var videoE = section.getElementsByTagName("video")[0];
	if (videoE != null) {
		tagMedia(section, videoE, videoE.parentNode, true, false)
	} else {
		var imgE = section.getElementsByTagName("img")[0];
		if (imgE != null) {
			tagMedia(section, imgE, imgE.parentNode, true, false)
		}
	}
}

/**
 * 获取图片数组
 */
function collectImgs(rootNode, userProfile, isPost) {
	if (collectLength != 0) {
		return false
	}
	collectRootNode = rootNode;
	collectLength = 1
	// 总长度通过指示器长度确定
	indicatorArrays = getIndicatorArray(rootNode, isPost)
	if (indicatorArrays != null) {
		collectLength = indicatorArrays.children.length;
	}
	if (collectLength == 1) {
		return false
	}
	// 当前下标
	nowIndex = getNowIndex(indicatorArrays);
	// 确保切换到第一页
	startReceiveData(userProfile, collectLength, false);
	return true
}

/**
 * 由Android端调用
 */
function startCollectData() {
	stepIndex = 1;
	isStartCollect = true;
	checkStep();
}

/**
 * 步骤检测
 */
function checkStep() {
	console.log("collectLength: " + collectLength);
	if (collectLength == 0) {
		return;
	}
	cancelTimeout();
	if (stepIndex == 1) { // 切换到第一页
		checkToLeft();
	} else if (stepIndex == 2) { // 开始搜集
		collectImg();
	} else if (stepIndex == 3) { // 复原
		restoreIndex();
	} else if (stepIndex == 4) { // 上报结束
		reportEndReceive();
	} else { // 结束
		resetStatus();
		console.log("complete !!!");
	}
}

function resetStatus() {
	collectRootNode = null;
	nowIndex = 0;
	collectLength = 0;
	indicatorArrays = null;
	timeoutId = -1;
	stepIndex = 0;
	duplicateCheck = null;
	// 检测次数
	checkTimes = 0;
}

/**
 * 第一步，切换到第一页
 */
function checkToLeft() {
	console.log("checkToLeft enter nowIndex: " + nowIndex);
	if (nowIndex != 0) { // 开始不是第一页
		var index = getNowIndex(indicatorArrays);
		console.log("checkToLeft index: " + index);
		if (index > 0) {
			// 触发左点击，最多等 @STATUS_INTERVAL_TIME 时间
			for (var i = 0; i <= index; i++) {
				leftBtnClick(collectRootNode, 0);
			}
			checkTimes = 0;
			timeoutCheck(50);
			return
		}
	}
	// 进入下一步
	stepIndex = 2;
	checkTimes = 0;
	checkStep();
}

/**
 * 第二步，收集图片
 */
function collectImg() {
	console.log("collectImg enter ");
	if (duplicateCheck == null) { // 初始化
		duplicateCheck = new Set();
	}
	// 搜集当前下标
	var index = getNowIndex(indicatorArrays);
	console.log("collectImg index: " + index);
	var dataTags = collectRootNode.getElementsByTagName("li");
	var length = dataTags.length;
	var dataSize = duplicateCheck.size;
	var hasEmpty = false;
	for (var i = 0; i < length; i++) {
		var videoData = getVideoUrl(dataTags[i])
		if (videoData != null) {
			console.log("videoData displayUrl: " + videoData.displayUrl + " videoUrl: " + videoData.videoUrl)
		}
		if (videoData != null) {
			if (!duplicateCheck.has(videoData.videoUrl)) {
				duplicateCheck.add(videoData.videoUrl)
				sendDataJson(JSON.stringify(videoData))
			}
		} else {
			var imgData = getImagUrl(dataTags[i]);
			if (imgData != null) {
				console.log("imgData displayUrl: " + imgData.displayUrl + " videoUrl: " + imgData.videoUrl)
			}
			if (imgData != null && !duplicateCheck.has(imgData.displayUrl)) {
				duplicateCheck.add(imgData.displayUrl)
				sendDataJson(JSON.stringify(imgData))
			} else {
				hasEmpty = true;
			}
		}
	}
	if (!hasEmpty && dataSize != duplicateCheck.size && collectLength != 2) { // 有空值，等文档变动自动修改
		checkTimes = 0;
		rightBtnClick(collectRootNode, 100);
	} else if (checkTimes != 0 && checkTimes % CHECK_MAX_TIMES == 0) {
		console.log("不得已触发点击");
		rightBtnClick(collectRootNode, 100);
	}
	// 检测是否完成
	if (duplicateCheck.size != collectLength) { // 没有完成，则超时继续检测
		timeoutCheck(50);
	} else { // 完成
		console.log("collectImg 收集完成");
		checkTimes = 0;
		stepIndex = 3;
		checkStep();
	}
}

/**
 * 第三步，复原
 */
function restoreIndex() {
	if (collectLength != 0) {
		isStartCollect = false;
		var index = getNowIndex(indicatorArrays);
		console.log("restoreIndex index: " + index + " nowIndex: " + nowIndex);
		if (index < nowIndex) { // 右点击
			var delayTime = 300;
			if (rightClassName == null) {
				delayTime = 700;
			}
			rightBtnClick(collectRootNode, delayTime);
			timeoutCheck(100);
			return;
		} else if (index > nowIndex) { // 左点击
			var delayTime = 300;
			if (leftClassName == null) {
				delayTime = 700;
			}
			leftBtnClick(collectRootNode, delayTime)
			timeoutCheck(100);
			return;
		}
		stepIndex = 4;
		checkStep();
	}
}

/**
 * 第四步，上报结束
 */
function reportEndReceive() {
	endReceiveData();
	stepIndex = 5;
	checkStep();
}

/**
 * 滑动到左侧时间最大时间检测
 */
function timeoutCheck(timeout) {
	if (checkTimes < STATUS_MAX_TIMES) {
		checkTimes++;
		timeoutId = setTimeout(function () {
			console.log("timeoutCheck over!! ");
			checkStep();
		}, timeout);
	} else { // 强制进入下一步
		console.log("强制进入下一步");
		checkTimes = 0;
		stepIndex++;
		checkStep();
	}
}

function cancelTimeout() {
	if (timeoutId != -1) {
		clearTimeout(timeoutId);
		timeoutId = -1;
	}
}

function isEmpty(obj) {
	if (typeof obj == "undefined" || obj == null || obj == "") {
		return true;
	} else {
		return false;
	}
}

/**
 * 获取图片Url
 */
function getImagUrl(imgParent) {
	if (imgParent != null) {
		var imgSelfs = imgParent.getElementsByTagName("img")
		for (var i = 0; i < imgSelfs.length; i++) {
			if (imgSelfs[i].className != "ins_dl_img") {
				var imgUrl = imgSelfs[i].src;
				console.log("getImagUrl imgUrl: " + imgUrl);
				if (!isEmpty(imgUrl)) {
					return {
						displayUrl: imgUrl,
						videoUrl: ""
					}
				}
			}
		}
	}
	return null;
}

/**
 * 获取视频Url
 */
function getVideoUrl(videoParent) {
	if (videoParent != null) {
		var videoTag = videoParent.getElementsByTagName("video")[0];
		if (videoTag != null) {
			var videoUrl = videoTag.src;
			if (!videoUrl) {
				var sources = videoTag.getElementsByTagName("source")
				if (sources.length > 0) {
					videoUrl = sources[0].src
				}
			}
			var thumbnailUrl = videoTag.poster
			if (thumbnailUrl && thumbnailUrl.indexOf("base64") >= 0) {
				//story视频
				var imgs = videoParent.getElementsByTagName("IMG")
				var thumbnailUrl = ""
				if (imgs.length > 0) {
					thumbnailUrl = imgs[0].src
				}
			}
			if (videoUrl && !videoUrl.startsWith("blob") && /[^\s]/.test(videoUrl)) {
				return {
					displayUrl: thumbnailUrl,
					videoUrl: videoUrl
				}
			} else {
				console.log("非法ins地址::: " + videoUrl)
			}
		}
	}
	return null;
}

/**
 * 通过指示器获取当前下标
 */
function getNowIndex(indicatorArrays) {
	console.log("getNowIndex enter ==> ");
	// classname最长的那个
	var confirmLength = -1
	var curIndex = -1
	if (indicatorArrays != null) {
		for (var i = 0; i < indicatorArrays.children.length; i++) {
			var curIndicator = indicatorArrays.children[i]
			console.log("getNowIndex i: " + i + " indicatorArrays[i]: " + curIndicator.className);
			var nowClassListLength = curIndicator.classList.length
			if (curIndex == -1) {
				confirmLength = nowClassListLength;
				curIndex = i
			} else if (nowClassListLength > confirmLength) {
				return i;
			} else if (nowClassListLength < confirmLength) {
				return curIndex;
			}
		}
	}
	return -1;
}

/**
 * 点击左侧按钮
 */
function leftBtnClick(parentNode, checkTime) {
	if (checkTime > 0) {
		var timestamp = new Date().getTime();
		console.log("leftBtnClick timestamp: " + timestamp + " lastStamp: " + lastStamp + " diff: " + (
			timestamp - lastStamp) + " checkTime: " + checkTime);
		if (timestamp - lastStamp < checkTime) {
			return;
		}
	}
	var leftBtn = getLeftBtnEle(parentNode);
	console.log("leftBtnClick enter ==> ");
	if (leftBtn != null) {
		leftBtn.click();
		console.log("leftBtnClick click");
		return true // 按钮存在
	} else {
		console.log("leftBtn is null")
	}
	return false; // 按钮不存在
}

/**
