fun encrypt(command: String, key: Int): String {
    val charArray = command.toCharArray()

    return with(StringBuilder()) {
        charArray.forEach {
            var ascii = it.toInt()
            ascii += key
            append(ascii.toChar())
        }
        toString()
    }
}

fun decrypt(command: String, key: Int): String {
    val charArray = command.toCharArray()
    return with(StringBuilder()) {
        charArray.forEach {
            var ascii = it.toInt()
            ascii -= key
            append(ascii.toChar())
        }
        toString()
    }
}

